INSERT INTO `anexo` (`cd_anexo`, `ds_titulo`, `ds_descricao`, `cd_situacao`) VALUES
(6, 'CLASSE I', '<div><span style=\"font-size:14px\"><span style=\"font-family:lucida sans unicode,lucida grande,sans-serif\"><small><strong>SPDA CLASSE&nbsp;I &bull;&nbsp;</strong>Destinado &agrave;s estruturas nas quais uma falha do sistema de prote&ccedil;&atilde;o pode causar danos &agrave;s estruturas vizinhas ou ao meio ambiente, como: dep&oacute;sitos de explosivos, materiais sujeitos &agrave; explos&atilde;o, material t&oacute;xico ao meio ambiente... etc.&nbsp;</small></span></span></div>\r\n', 1),
(7, 'CLASSE II', '<div><span style=\"font-size:14px\"><small><span style=\"font-family:lucida sans unicode,lucida grande,sans-serif\"><strong>SPDA CLASSE&nbsp;II &bull; </strong>Destinados &agrave;s estruturas cujos danos em caso de falha ser&atilde;o elevados ou haver&aacute; destrui&ccedil;&atilde;o de bens insubstitu&iacute;veis e/ou de valor hist&oacute;rico, mas em qualquer caso se restringir&atilde;o &agrave; estrutura e seu conte&uacute;do, como: museus, escolas, gin&aacute;sios esportivos, est&aacute;dio de futebol... etc.&nbsp;</span></small></span></div>\r\n', 1),
(8, 'CLASSE III', '<div><span style=\"font-size:14px\"><span style=\"font-family:lucida sans unicode,lucida grande,sans-serif\"><small><strong>SPDA CLASSE&nbsp;III &bull; </strong>Destinada &agrave;s estruturas de uso comum, como: resid&ecirc;ncias, escrit&oacute;rios, f&aacute;bricas sem risco de explos&atilde;o ou de risco... etc.</small></span></span></div>\r\n', 1),
(9, 'CLASSE IV', '<div><span style=\"font-size:14px\"><span style=\"font-family:lucida sans unicode,lucida grande,sans-serif\"><small><strong>SPDA CLASSE&nbsp;IV &bull; </strong>Destinadas &agrave;s estruturas constru&iacute;das de material n&atilde;o inflam&aacute;vel, com pouco acesso de pessoas e com conte&uacute;do n&atilde;o inflam&aacute;vel, como: dep&oacute;sitos de concreto e com conte&uacute;do n&atilde;o inflam&aacute;vel, estoque de produtos agr&iacute;colas... etc.</small></span></span></div>\r\n', 1);
