(9, '1CAPT - Franklin captor + Mastro (instalação)', '<ul>\r\n	<li style=\"text-align:justify\">\r\n	<div><span style=\"font-family:lucida sans unicode,lucida grande,sans-serif\"><span style=\"font-size:11px\"><big><small><span style=\"color:rgb(0, 0, 0)\">Ser&aacute; realizada&nbsp;a INSTALA&Ccedil;&Atilde;O&nbsp;de conjunto(s) de Mastro(s)&nbsp;e&nbsp;Captor(es) do tipo Franklin para receber(em) as descargas atmosf&eacute;ricas (raios) e conduz&iacute;-las entre os demais subsistemas&nbsp;que comp&otilde;e(m) o p&aacute;ra-raio (SPDA - Sistema de Prote&ccedil;&atilde;o contra Descarga&nbsp;Atmosf&eacute;rica), conforme preconiza item 3.9 da NBR 5.419/2015;</span></small></big></span></span></div>\r\n	</li>\r\n</ul>\r\n', 1, 0, 0, 0),
(10, '1CAPT - Terminal Aéreo (instalação)', '<ul>\r\n	<li>\r\n	<div style=\"text-align: justify;\"><span style=\"font-family:lucida sans unicode,lucida grande,sans-serif\"><span style=\"font-size:11px\"><big><small><span style=\"color:rgb(0, 0, 0)\">Ser&aacute; realizada&nbsp;a INSTALA&Ccedil;&Atilde;O de Terminal(is) A&eacute;reo(s) em barra de alum&iacute;nio 7/8&quot; x 1/8&quot;&nbsp;sobre a cobertura, com o intuito de captar a corrente proveniente das&nbsp;descargas atmosf&eacute;ricas (raios) e conduz&iacute;-las entre os demais subsistemas que comp&otilde;e(m) o p&aacute;ra-raio (SPDA - Sistema de Prote&ccedil;&atilde;o contra Descarga&nbsp;Atmosf&eacute;rica);</span></small></big></span></span></div>\r\n	</li>\r\n</ul>\r\n', 1, 0, 0, 0),
(11, '1CAPT - Anel perimetral de Captação (recomposição)', '<ul>\r\n	<li>\r\n	<div style=\"text-align: justify;\"><span style=\"font-family:lucida sans unicode,lucida grande,sans-serif\"><span style=\"font-size:11px\"><small><big><span style=\"color:rgb(0, 0, 0)\">Realiza&ccedil;&atilde;o da recomposi&ccedil;&atilde;o do anel perimetral de capta&ccedil;&atilde;o&nbsp;sobre a cobertura, reintegrando pontos deteriorados e realizando a manuten&ccedil;&atilde;o e/ou reinstala&ccedil;&atilde;o de componentes, se&nbsp; necess&aacute;rio for;</span></big></small></span></span></div>\r\n	</li>\r\n</ul>\r\n', 1, 0, 0, 0),
(12, '1CAPT - Elementos metálicos cobertura (adequação)', '<ul>\r\n	<li>\r\n	<div><span style=\"font-family:lucida sans unicode,lucida grande,sans-serif\"><span style=\"font-size:11px\"><big><small><span style=\"color:rgb(0, 0, 0)\">Ser&aacute; realizado ajustes de altura&nbsp;em elemento(s) met&aacute;lico(s) existente(s) na cobertura e que estiver(em) na mesma e/ou acima do n&iacute;vel do(s)&nbsp;conjunto(s) de Mastro(s)&nbsp;e&nbsp;Captor(es) do tipo Franklin, baixando seu n&iacute;vel e deixando-o(s) em altura inferior(es) para que estejam dentro do raio de prote&ccedil;&atilde;o coberto pelo Franklin captor;</span></small></big></span></span></div>\r\n	</li>\r\n</ul>\r\n', 1, 0, 0, 0),
(13, '1CAPT - Franklin captor + Mastro (adequação)', '<ul>\r\n	<li>\r\n	<div style=\"text-align: justify;\"><span style=\"font-family:lucida sans unicode,lucida grande,sans-serif\"><span style=\"font-size:11px\"><big><small><span style=\"color:rgb(0, 0, 0)\">Ser&aacute; realizada&nbsp;a ADEQUA&Ccedil;&Atilde;O de conjunto(s) de Mastro(s)&nbsp;e&nbsp;Captor(es) do tipo Franklin j&aacute; existente(s); assim como a&nbsp;ancoragem, alinhamento e galvaniza&ccedil;&atilde;o do(s) mesmo(s)&nbsp;para evitar futuras corros&otilde;es (item 5.1.5.3 da NBR 5.419/2015) e/ou troca do(s) captor(es), quando necess&aacute;rio; assim como manuten&ccedil;&atilde;o em todo o conjunto do subsistema de capta&ccedil;&atilde;o;</span></small></big></span></span></div>\r\n	</li>\r\n</ul>\r\n', 1, 0, 0, 0),
(14, '1CAPT - Terminal Aéreo (manutenção)', '<ul>\r\n	<li>\r\n	<div style=\"text-align: justify;\"><span style=\"font-family:lucida sans unicode,lucida grande,sans-serif\"><span style=\"font-size:11px\"><big><small><span style=\"color:rgb(0, 0, 0)\">Ser&aacute; realizada&nbsp;a MANUTEN&Ccedil;&Atilde;O do(s)&nbsp;Terminal(is) A&eacute;reo(s) em barra de alum&iacute;nio 7/8&quot; x 1/8&quot;&nbsp;sobre a cobertura; troca dos terminais danificados, quando necess&aacute;rio; galvaniza&ccedil;&atilde;o&nbsp; a frio dos mesmos; assim como&nbsp;dos demais elementos met&aacute;licos de capta&ccedil;&atilde;o que necessitarem de manuten&ccedil;&atilde;o;</span></small></big></span></span></div>\r\n	</li>\r\n</ul>\r\n', 1, 0, 0, 0),
(15, '1CAPT - Luz Piloto (manutenção)', '<ul>\r\n	<li>\r\n	<div style=\"text-align: justify;\"><span style=\"font-family:lucida sans unicode,lucida grande,sans-serif\"><span style=\"font-size:11px\"><big><small><span style=\"color:rgb(0, 0, 0)\">Realiza&ccedil;&atilde;o da MANUTEN&Ccedil;&Atilde;O na Luz Piloto, quando houver,&nbsp;na cobertura do&nbsp;im&oacute;vel, verificando o bom funcionamento da fotoc&eacute;lula, alimenta&ccedil;&atilde;o e demais componentes da mesma; assim como, realiza&ccedil;&atilde;o da troca de componentes,&nbsp;<big><small><span style=\"color:rgb(0, 0, 0)\">se necess&aacute;rio for;</span></small></big></span></small></big></span></span></div>\r\n	</li>\r\n</ul>\r\n', 1, 0, 0, 0),
(16, '0 - SPDA Radioativo (retirada)', '<ul>\r\n	<li>\r\n	<div style=\"text-align: justify;\"><span style=\"font-size:10px\"><span style=\"font-family:lucida sans unicode,lucida grande,sans-serif\"><big><span style=\"color:rgb(0, 0, 0)\">Retirada dos P&aacute;ra-raios radioativos, quando houver,&nbsp;e o encaminhamento ao IPEN-USP conforme normas para descarte:</span></big></span></span></div>\r\n	</li>\r\n</ul>\r\n\r\n<ol>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:8px\"><span style=\"font-family:lucida sans unicode,lucida grande,sans-serif\"><big><span style=\"color:rgb(0, 0, 0)\">Usar material necess&aacute;rio.</span></big></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:8px\"><span style=\"font-family:lucida sans unicode,lucida grande,sans-serif\"><big><span style=\"color:rgb(0, 0, 0)\">Remover o para-raios da haste, usando a luva.</span></big></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:8px\"><span style=\"font-family:lucida sans unicode,lucida grande,sans-serif\"><big><span style=\"color:rgb(0, 0, 0)\">Colocar o para-raios no saco pl&aacute;stico.</span></big></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:8px\"><span style=\"font-family:lucida sans unicode,lucida grande,sans-serif\"><big><span style=\"color:rgb(0, 0, 0)\">Descal&ccedil;ar a luva e coloc&aacute;-la no saco pl&aacute;stico e lavar as m&atilde;os com &aacute;gua e sab&atilde;o.</span></big></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:8px\"><span style=\"font-family:lucida sans unicode,lucida grande,sans-serif\"><big><span style=\"color:rgb(0, 0, 0)\">Fechar o saco com fita adesiva.</span></big></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:8px\"><span style=\"font-family:lucida sans unicode,lucida grande,sans-serif\"><big><span style=\"color:rgb(0, 0, 0)\">Colocar o saco na lata e encher os espa&ccedil;os vazios com o material de enchimento. Cada lata pode conter somente um para-raios.</span></big></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:8px\"><span style=\"font-family:lucida sans unicode,lucida grande,sans-serif\"><big><span style=\"color:rgb(0, 0, 0)\">Antes de fechar a tampa, colocar a etiqueta com o S&Iacute;MBOLO DE RADIA&Ccedil;&Atilde;O.</span></big></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:8px\"><span style=\"font-family:lucida sans unicode,lucida grande,sans-serif\"><big><span style=\"color:rgb(0, 0, 0)\">Colar a etiqueta de endere&ccedil;amento.</span></big></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:8px\"><span style=\"font-family:lucida sans unicode,lucida grande,sans-serif\"><big><span style=\"color:rgb(0, 0, 0)\">Preencher o documento de transporte DECLARA&Ccedil;&Atilde;O DO EXPEDIDOR.</span></big></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:8px\"><span style=\"font-family:lucida sans unicode,lucida grande,sans-serif\"><big><span style=\"color:rgb(0, 0, 0)\">Imprimir 4 (quatro) vias da declara&ccedil;&atilde;o: 1 para o IPEN; 1 para o remetente; 1 para o transportador; 1 para a CNEN.</span></big></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:8px\"><span style=\"font-family:lucida sans unicode,lucida grande,sans-serif\"><big><span style=\"color:rgb(0, 0, 0)\">Emitir uma Nota Fiscal de simples remessa, caso o expedidor seja Pessoa Jur&iacute;dica, ou uma Carta de Encaminhamento em duas vias, caso o expedidor seja Pessoa F&iacute;sica, especificando o n&uacute;mero de para-raios e o endere&ccedil;o de onde foram retirados.</span></big></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:8px\"><span style=\"font-family:lucida sans unicode,lucida grande,sans-serif\"><big><span style=\"color:rgb(0, 0, 0)\">Endere&ccedil;ar &agrave;: COMISS&Atilde;O NACIONAL DE ENERGIA NUCLEAR Avenida Prof. Lineu Prestes, 2242 - CIDADE UNIVERSIT&Aacute;RIA CEP: 05508-000 &ndash; S&Atilde;O PAULO, SP CGC: 00.402.552/0005-50 INSCR. ESTADUAL: 110.670.880.110</span></big></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:8px\"><span style=\"font-family:lucida sans unicode,lucida grande,sans-serif\"><big><span style=\"color:rgb(0, 0, 0)\">Montar um ENVELOPE DE TRANSPORTE colando as folhas do envelope 1&rdquo; e envelope 2&rdquo; nas duas faces externas do envelope de papel &ldquo;kraft&rdquo;.</span></big></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:8px\"><span style=\"font-family:lucida sans unicode,lucida grande,sans-serif\"><big><span style=\"color:rgb(0, 0, 0)\">Colocar no ENVELOPE DE TRANSPORTE os seguintes documentos: a) Declara&ccedil;&atilde;o do expedidor b) Ficha de emerg&ecirc;ncia c) Certificado de aprova&ccedil;&atilde;o especial para embalado. d) Nota fiscal ou carta de encaminhamento.</span></big></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:8px\"><span style=\"font-family:lucida sans unicode,lucida grande,sans-serif\"><big><span style=\"color:rgb(0, 0, 0)\">Comunicar a data de entrega, com pelo menos um dia de anteced&ecirc;ncia, pelos telefones (0xx11) 3133-9745 ou (0xx11) 3133-9749. Caso o transporte seja feito por transportadora, informar o nome da transportadora, n&uacute;mero do conhecimento e a quantidade de para-raios.</span></big></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:8px\"><span style=\"font-family:lucida sans unicode,lucida grande,sans-serif\"><big><span style=\"color:rgb(0, 0, 0)\">Transportar os para-raios no compartimento de carga (ve&iacute;culo de carga) ou no porta-malas (ve&iacute;culo de passeio). O envelope contendo a documenta&ccedil;&atilde;o deve ser levado pelo motorista do ve&iacute;culo. O hor&aacute;rio de recebimento de para-raios no IPEN &eacute;: 08:00 &ndash; 11:00 e 13:00 &ndash; 16:00.&nbsp;</span></big></span></span></li>\r\n</ol>\r\n', 1, 0, 0, 0),
(18, '2DESC - Subsistema de Descida (manutenção)', '<ul>\r\n	<li>\r\n	<div><span style=\"font-family:lucida sans unicode,lucida grande,sans-serif\"><span style=\"font-size:11px\"><big><small><span style=\"color:rgb(0, 0, 0)\">Ser&aacute; realizada a MANUTEN&Ccedil;&Atilde;O do subsistema de descida j&aacute; existente no im&oacute;vel; assim como, reinstala&ccedil;&atilde;o e/ou troca das mesmas, se necess&aacute;rio for;</span></small></big></span></span></div>\r\n	</li>\r\n</ul>\r\n', 1, 0, 0, 0),
(19, '2DESC - Subsistema de Descida (instalação)', '<ul>\r\n	<li>\r\n	<div><span style=\"font-family:lucida sans unicode,lucida grande,sans-serif\"><span style=\"font-size:11px\"><big><small><span style=\"color:rgb(0, 0, 0)\">Ser&aacute;&nbsp;realizada a INSTALA&Ccedil;&Atilde;O&nbsp;do subsistema de descida em barras de alum&iacute;nio 7/8&quot; x 1/8&quot; para interlig&aacute;-las&nbsp;ao subsistema de aterramento por meio de cabo de cobre nu, com o objetivo de conduzir a corrente da(s) descarga(s) atmosf&eacute;rica(s) a dissipa&ccedil;&atilde;o no solo;</span></small></big></span></span></div>\r\n	</li>\r\n</ul>\r\n', 1, 0, 0, 0),
(20, '2DESC - Subsistema de Descida (identificação)', '<ul>\r\n	<li>\r\n	<div><span style=\"font-family:lucida sans unicode,lucida grande,sans-serif\"><span style=\"font-size:11px\"><big><small><span style=\"color:rgb(0, 0, 0)\">Ser&aacute; realizada a IDENTIFICA&Ccedil;&Atilde;O do subsistema de descida por etiquetas apresentando a validade de implanta&ccedil;&atilde;o, como preconiza a NBR 5.419, para acompanhamento e transpar&ecirc;ncia do prazo de a&ccedil;&otilde;es preventivas e/ou corretivas;</span></small></big></span></span></div>\r\n	</li>\r\n</ul>\r\n', 1, 0, 0, 0),
(21, '4INTER - INTERLIGAÇÕES SPDA', '<ul>\r\n	<li style=\"text-align:justify\">\r\n	<div><span style=\"font-family:lucida sans unicode,lucida grande,sans-serif\"><span style=\"font-size:11px\"><big><small><span style=\"color:rgb(0, 0, 0)\">&Eacute; necess&aacute;rio&nbsp;que TODOS E QUAISQUER elementos e estruturas&nbsp;met&aacute;licas existentes no im&oacute;vel&nbsp;estejam INTERLIGADOS ao SPDA, assim como equipotencializados;&nbsp;tal procedimento torna-se fundamental, pois, em uma eventual a&ccedil;&atilde;o de uma DEA (Descarga El&eacute;trica Atmosf&eacute;rica),&nbsp;poder&atilde;o&nbsp;sofrer danos nestes pontos&nbsp;ou mesmo&nbsp;reflexos&nbsp;secund&aacute;rias&nbsp;como indu&ccedil;&atilde;o de correntes perigosas, ?<span style=\"color:rgb(0, 0, 0); font-family:lucida sans unicode,lucida grande,sans-serif; font-size:11px\">conforme as determina&ccedil;&otilde;es do item 6.2&nbsp;NBR&nbsp;5.419-3/2015;</span></span></small></big></span></span></div>\r\n	</li>\r\n</ul>\r\n', 1, 0, 0, 0),
(22, '3ATERR - Anel perimetral de solo (instalação)', '<ul>\r\n	<li>\r\n	<div style=\"text-align: justify;\"><span style=\"font-family:lucida sans unicode,lucida grande,sans-serif\"><span style=\"font-size:11px\"><big><small><span style=\"color:rgb(0, 0, 0)\">Ser&aacute; realizada a INSTALA&Ccedil;&Atilde;O do anel perimetral em cabo de cobre nu de&nbsp;50 mm&sup2; no solo, a uma profundidade m&iacute;nima de 60 cm, como preconiza o item 5.1.3.2.1 da NBR 5.419&nbsp;&uacute;ltima vers&atilde;o revisada em 2015;</span></small></big></span></span></div>\r\n	</li>\r\n</ul>\r\n', 1, 0, 0, 0),
(23, '3ATERR - Anel perimetral de solo (manutenção)', '<ul>\r\n	<li>\r\n	<div style=\"text-align: justify;\"><span style=\"font-family:lucida sans unicode,lucida grande,sans-serif\"><span style=\"font-size:11px\"><big><small><span style=\"color:rgb(0, 0, 0)\">Ser&aacute; realizada a MANUTEN&Ccedil;&Atilde;O de todo o anel perimetral no solo; assim como, recomposi&ccedil;&atilde;o do mesmo, se necess&aacute;rio for;</span></small></big></span></span></div>\r\n	</li>\r\n</ul>\r\n', 1, 0, 0, 0),
(24, '3ATERR - Subsistema de aterramento (manutenção)', '<ul>\r\n	<li>\r\n	<div style=\"text-align: justify;\"><span style=\"font-family:lucida sans unicode,lucida grande,sans-serif\"><span style=\"font-size:11px\"><big><small><span style=\"color:rgb(0, 0, 0)\">Ser&aacute; realizada a MANUTEN&Ccedil;&Atilde;O no subsistema de aterramento; assim como, se necess&aacute;rio: recomposi&ccedil;&atilde;o do mesmo, troca de conector(es) e terminal(is) GAR presente nas caixas de inspe&ccedil;&atilde;o, troca de </span></small></big><span style=\"color:rgb(0, 0, 0)\">haste(s) copperweld 5/8&quot;x2,40m;</span></span></span></div>\r\n	</li>\r\n</ul>\r\n', 1, 0, 0, 0),
(25, '3ATERR - Caixa de Inspeção (instalação)', '<ul>\r\n	<li>\r\n	<div style=\"text-align: justify;\"><span style=\"font-size:12px\"><small><span style=\"font-family:lucida sans unicode,lucida grande,sans-serif\"><big><span style=\"color:rgb(0, 0, 0)\">Ser&aacute; realizada a INSTALA&Ccedil;&Atilde;O de caixa(s) de inspe&ccedil;&atilde;o no solo, di&Acirc;metro de 290 mm com tampa de PVC; assim como, identifica&ccedil;&atilde;o da(s) mesma(s) </span></big><big><span style=\"color:rgb(0, 0, 0)\">por etiquetas apresentando a validade de implanta&ccedil;&atilde;o delas,&nbsp;como preconiza a NBR 5.419, para acompanhamento e transpar&ecirc;ncia do prazo de a&ccedil;&otilde;es preventivas e/ou corretivas; assim como, cravamento de haste(s) copperweld 5/8&quot; x 2,40m, conforme determina&ccedil;&otilde;es do item 5.5.3 da NBR&nbsp;5.419-3/2015;</span></big></span></small></span></div>\r\n	</li>\r\n</ul>\r\n', 1, 0, 0, 0),
(26, 'INSPEÇÃO TÉCNICA (SPDA)', '<ul>\r\n	<li>\r\n	<div><small><span style=\"font-family:lucida sans unicode,lucida grande,sans-serif\"><span style=\"font-size:11px\"><span style=\"color:rgb(0, 0, 0)\">A realiza&ccedil;&atilde;o da INSPE&Ccedil;&Atilde;O T&Eacute;CNICA &eacute; feita por nosso profissional, devidamente habilitado, para verificar as conformidades e/ou inconformidades presente no sistema de prote&ccedil;&atilde;o (SPDA) j&aacute; existente no im&oacute;vel; </span></span></span></small></div>\r\n	</li>\r\n</ul>\r\n', 1, 0, 0, 0),
(27, 'ELET - Diagrama Unifilar', '<ul>\r\n	<li>\r\n	<div><span style=\"font-size:14px\"><span style=\"font-family:lucida sans unicode,lucida grande,sans-serif\"><small><span style=\"color:rgb(0, 0, 0)\">Elabora&ccedil;&atilde;o de Diagrama(s) Unifilar(es), atualizado(s), das instala&ccedil;&otilde;es el&eacute;tricas presente&nbsp;no im&oacute;vel,&nbsp;com as devidas identifica&ccedil;&otilde;es e especifica&ccedil;&otilde;es do(s) Quadro(s) de Energia, conforme recomenda o item 10.2.3 da&nbsp;NR-10;</span></small></span></span></div>\r\n	</li>\r\n</ul>\r\n', 1, 0, 0, 0),
(28, 'ELET - Laudo NR10', '<div><span style=\"font-size:12px\"><small><span style=\"font-family:lucida sans unicode,lucida grande,sans-serif\"><big><span style=\"color:rgb(0, 0, 0)\">Elabora&ccedil;&atilde;o de <strong>Laudo das instala&ccedil;&otilde;es el&eacute;tricas</strong> (de acordo a NR-10)&nbsp;contemplando ART do engenheiro respons&aacute;vel,&nbsp;documento esse resultado da inspe&ccedil;&atilde;o geral de&nbsp;todos os quadros e pain&eacute;is el&eacute;tricos presente no respectivo im&oacute;vel, a fim de atestar as conformidades e/ou inconformidades. Ser&atilde;o analisados:</span></big></span></small></span></div>\r\n\r\n<ul>\r\n	<li>\r\n	<div><span style=\"font-size:12px\"><small><span style=\"font-family:lucida sans unicode,lucida grande,sans-serif\"><big><span style=\"color:rgb(0, 0, 0)\">Passagem de cabos em canaletas;</span></big></span></small></span></div>\r\n	</li>\r\n	<li>\r\n	<div><span style=\"font-size:12px\"><small><span style=\"font-family:lucida sans unicode,lucida grande,sans-serif\"><big><span style=\"color:rgb(0, 0, 0)\">sinaliza&ccedil;&atilde;o;</span></big></span></small></span></div>\r\n	</li>\r\n	<li>\r\n	<div><span style=\"font-size:12px\"><small><span style=\"font-family:lucida sans unicode,lucida grande,sans-serif\"><big><span style=\"color:rgb(0, 0, 0)\">identifica&ccedil;&atilde;o de circuito;</span></big></span></small></span></div>\r\n	</li>\r\n	<li>\r\n	<div><span style=\"font-size:12px\"><small><span style=\"font-family:lucida sans unicode,lucida grande,sans-serif\"><big><span style=\"color:rgb(0, 0, 0)\">travas dos quadros;</span></big></span></small></span></div>\r\n	</li>\r\n	<li>\r\n	<div><span style=\"font-size:12px\"><small><span style=\"font-family:lucida sans unicode,lucida grande,sans-serif\"><big><span style=\"color:rgb(0, 0, 0)\">se existe&nbsp;diagrama unifilar internamente do quadro;</span></big></span></small></span></div>\r\n	</li>\r\n	<li>\r\n	<div><span style=\"font-size:12px\"><small><span style=\"font-family:lucida sans unicode,lucida grande,sans-serif\"><big><span style=\"color:rgb(0, 0, 0)\">exist&ecirc;ncia de prote&ccedil;&atilde;o das partes energizadas com barreira isolante (acr&iacute;lico);</span></big></span></small></span></div>\r\n	</li>\r\n	<li>\r\n	<div><span style=\"font-size:12px\"><small><span style=\"font-family:lucida sans unicode,lucida grande,sans-serif\"><big><span style=\"color:rgb(0, 0, 0)\">estado dos&nbsp;cabos;</span></big></span></small></span></div>\r\n	</li>\r\n	<li>\r\n	<div><span style=\"font-size:12px\"><small><span style=\"font-family:lucida sans unicode,lucida grande,sans-serif\"><big><span style=\"color:rgb(0, 0, 0)\">conex&otilde;es e terminais;</span></big></span></small></span></div>\r\n	</li>\r\n	<li>\r\n	<div><span style=\"font-size:12px\"><small><span style=\"font-family:lucida sans unicode,lucida grande,sans-serif\"><big><span style=\"color:rgb(0, 0, 0)\">aterramento;</span></big></span></small></span></div>\r\n	</li>\r\n	<li>\r\n	<div><span style=\"font-size:12px\"><small><span style=\"font-family:lucida sans unicode,lucida grande,sans-serif\"><big><span style=\"color:rgb(0, 0, 0)\">continuidade dos equipamentos;</span></big></span></small></span></div>\r\n	</li>\r\n	<li>\r\n	<div><span style=\"font-size:12px\"><small><span style=\"font-family:lucida sans unicode,lucida grande,sans-serif\"><big><span style=\"color:rgb(0, 0, 0)\">poss&iacute;veis interfer&ecirc;ncias;</span></big></span></small></span></div>\r\n	</li>\r\n	<li>\r\n	<div><span style=\"font-size:12px\"><small><span style=\"font-family:lucida sans unicode,lucida grande,sans-serif\"><big><span style=\"color:rgb(0, 0, 0)\">adequa&ccedil;&otilde;es necess&aacute;rias;</span></big></span></small></span></div>\r\n	</li>\r\n	<li>\r\n	<div><span style=\"font-size:12px\"><small><span style=\"font-family:lucida sans unicode,lucida grande,sans-serif\"><big><span style=\"color:rgb(0, 0, 0)\">layout de procedimentos de opera&ccedil;&atilde;o;</span></big></span></small></span></div>\r\n	</li>\r\n	<li>\r\n	<div><span style=\"font-size:12px\"><small><span style=\"font-family:lucida sans unicode,lucida grande,sans-serif\"><big><span style=\"color:rgb(0, 0, 0)\"><span style=\"color:rgb(0, 0, 0)\">Verifica&ccedil;&atilde;o&nbsp;se as &aacute;reas de alta tens&atilde;o (quando houver) est&atilde;o devidamente isoladas, sinalizadas e&nbsp;com restri&ccedil;&atilde;o de acesso;</span></span></big></span></small></span></div>\r\n	</li>\r\n	<li>\r\n	<div><span style=\"font-size:12px\"><small><span style=\"font-family:lucida sans unicode,lucida grande,sans-serif\"><big><span style=\"color:rgb(0, 0, 0)\"><span style=\"color:rgb(0, 0, 0)\"><span style=\"color:rgb(0, 0, 0)\">Verifica&ccedil;&atilde;o&nbsp;se tomadas e plugs est&atilde;o padronizados e com devida identifica&ccedil;&atilde;o de tens&atilde;o;</span></span></span></big></span></small></span></div>\r\n	</li>\r\n	<li>\r\n	<div><span style=\"font-size:12px\"><small><span style=\"font-family:lucida sans unicode,lucida grande,sans-serif\"><big><span style=\"color:rgb(0, 0, 0)\"><span style=\"color:rgb(0, 0, 0)\"><span style=\"color:rgb(0, 0, 0)\"><span style=\"color:rgb(0, 0, 0)\">Verifica&ccedil;&atilde;o se as&nbsp;passagens de fios nas eletrocalhas&nbsp;est&atilde;o em bom estado, bem acomodadas e fechadas, e etc;</span></span></span></span></big></span></small></span></div>\r\n	</li>\r\n</ul>\r\n', 1, 0, 0, 0),
(29, 'ELET - Quadro Elétrico (adequação)', '<div><span style=\"font-size:14px\"><span style=\"color:#000000\"><span style=\"font-family:lucida sans unicode,lucida grande,sans-serif\"><small>Ser&aacute; realizado:</small></span></span></span></div>\r\n\r\n<ul>\r\n	<li>\r\n	<div><span style=\"font-size:14px\"><span style=\"color:#000000\"><span style=\"font-family:lucida sans unicode,lucida grande,sans-serif\"><small>Manuten&ccedil;&atilde;o e troca de cabos danificados, quando necess&aacute;rio;</small></span></span></span></div>\r\n	</li>\r\n	<li>\r\n	<div><span style=\"font-size:14px\"><span style=\"color:#000000\"><span style=\"font-family:lucida sans unicode,lucida grande,sans-serif\"><small>Organiza&ccedil;&atilde;o de cabos em canaletas;</small></span></span></span></div>\r\n	</li>\r\n	<li>\r\n	<div><span style=\"font-size:14px\"><span style=\"color:#000000\"><span style=\"font-family:lucida sans unicode,lucida grande,sans-serif\"><small>Manuten&ccedil;&atilde;o e reaperto de conex&otilde;es, onde houver necessidade;</small></span></span></span></div>\r\n	</li>\r\n	<li>\r\n	<div><span style=\"font-size:14px\"><span style=\"color:#000000\"><span style=\"font-family:lucida sans unicode,lucida grande,sans-serif\"><small>Troca de conectores e terminais em mau estado ou com irregularidades;</small></span></span></span></div>\r\n	</li>\r\n	<li>\r\n	<div><span style=\"font-size:14px\"><span style=\"color:#000000\"><span style=\"font-family:lucida sans unicode,lucida grande,sans-serif\"><small>Etiquetar componentes el&eacute;tricos;</small></span></span></span></div>\r\n	</li>\r\n	<li>\r\n	<div><span style=\"font-size:14px\"><span style=\"color:#000000\"><span style=\"font-family:lucida sans unicode,lucida grande,sans-serif\"><small>Faseamento el&eacute;trico da entrada principal;</small></span></span></span></div>\r\n	</li>\r\n	<li>\r\n	<div><span style=\"font-size:14px\"><span style=\"color:#000000\"><span style=\"font-family:lucida sans unicode,lucida grande,sans-serif\"><small>Limpeza dos disjuntores;</small></span></span></span></div>\r\n	</li>\r\n	<li>\r\n	<div><span style=\"font-size:14px\"><span style=\"color:#000000\"><span style=\"font-family:lucida sans unicode,lucida grande,sans-serif\"><small>Veda&ccedil;&atilde;o de elementos com poss&iacute;vel entrada de insetos para evitar curto-circuito nos quadros;</small></span></span></span></div>\r\n	</li>\r\n	<li>\r\n	<div><span style=\"font-size:14px\"><span style=\"color:#000000\"><span style=\"font-family:lucida sans unicode,lucida grande,sans-serif\"><small>Verifica&ccedil;&atilde;o de elementos com mau contato el&eacute;trico.</small></span></span></span></div>\r\n	</li>\r\n</ul>\r\n', 1, 0, 0, 0),
(30, 'ELET - DPS (instalação e aterramento)', '<ul>\r\n	<li>\r\n	<div><small><span style=\"font-size:11px\"><span style=\"font-family:lucida sans unicode,lucida grande,sans-serif\"><span style=\"color:rgb(0, 0, 0)\">Ser&aacute; realizada a INSTALA&Ccedil;&Atilde;O de DPS (Dispositivo de Prote&ccedil;&atilde;o contra Surtos) com&nbsp;aterramento dedicado, conforme&nbsp;norma(s) t&eacute;cnica(s) vigente(s), na(s) linha(s) de energia e/ou telecomunica&ccedil;&atilde;o.</span></span></span></small></div>\r\n	</li>\r\n</ul>\r\n', 1, 0, 0, 0),
(31, 'ELET - Eletrocalhas (instalação)', '<ul>\r\n	<li>\r\n	<div><small><span style=\"font-size:11px\"><span style=\"font-family:lucida sans unicode,lucida grande,sans-serif\"><span style=\"color:rgb(0, 0, 0)\">Ser&aacute; realizada a INSTALA&Ccedil;&Atilde;O de eletrocalhas galvanizadas a fogo para uma melhor acomoda&ccedil;&atilde;o dos cabos el&eacute;tricos, para a seguran&ccedil;a de todos os usu&aacute;rios;</span></span></span></small></div>\r\n	</li>\r\n</ul>\r\n', 1, 0, 0, 0),
(32, '1CAPT - Luz Piloto (instalação)', '<ul>\r\n	<li>\r\n	<div style=\"text-align: justify;\"><span style=\"font-family:lucida sans unicode,lucida grande,sans-serif\"><span style=\"font-size:11px\"><big><small><span style=\"color:rgb(0, 0, 0)\">Realiza&ccedil;&atilde;o da INSTALA&Ccedil;&Atilde;O&nbsp;da Luz Piloto com fotoc&eacute;lula no im&oacute;vel; caso haja possibilidade, ser&aacute; fixada&nbsp;junto ao conjunto do elemento de capta&ccedil;&atilde;o do SPDA (Mastro + Franklin captor)<big><small>;</small></big></span></small></big></span></span></div>\r\n	</li>\r\n</ul>\r\n', 1, 0, 0, 0),
(33, 'ELET - Placa acrílica (instalação)', '<ul>\r\n	<li>\r\n	<div><span style=\"font-size:14px\"><small><span style=\"font-family:lucida sans unicode,lucida grande,sans-serif\">Ser&aacute; realizada a INSTALA&Ccedil;&Atilde;O de placa(s) acr&iacute;lica(s) nas partes ativas&nbsp;com o intuito de evitar acidentes el&eacute;tricos por contato com as mesmas, onde&nbsp;situam-se dentro dos pain&eacute;is el&eacute;tricos;&nbsp;segundo a NR-10&nbsp;&eacute; obrigat&oacute;ria a instala&ccedil;&atilde;o de elementos que contribuam para o isolamento das partes ativas (com tens&atilde;o el&eacute;trica e/ou voltagem) para com o profissional habilitado.</span></small></span></div>\r\n	</li>\r\n</ul>\r\n', 1, 0, 0, 0),
(34, 'EQUIPOTENCIALIZAÇÃO GERAL', '<ul>\r\n	<li>\r\n	<div><span style=\"font-size:14px\"><small><span style=\"font-family:lucida sans unicode,lucida grande,sans-serif\"><span style=\"color:rgb(0, 0, 0)\">Com o intuito de manter uma mesma diferen&ccedil;a de potencial el&eacute;trico entre corpos condutores de eletricidade, ser&aacute; realizada a EQUIPOTENCIALIZA&Ccedil;&Atilde;O de todas as massas existentes no im&oacute;vel de interesse, conforme determina&ccedil;&otilde;es do item 6.2 da NBR&nbsp;5.419-3/2015;</span></span></small></span></div>\r\n	</li>\r\n</ul>\r\n', 1, 0, 0, 0),
(35, 'ELET - Quadro Elétrico (troca)', '<ul>\r\n	<li>\r\n	<div><small><span style=\"font-family:lucida sans unicode,lucida grande,sans-serif\"><span style=\"font-size:11px\">Ser&aacute; realizada a TROCA do quadro de energia, assim como&nbsp;a INSTALA&Ccedil;&Atilde;O do mesmo;</span></span></small></div>\r\n	</li>\r\n</ul>\r\n', 1, 0, 0, 0),
(36, 'DETECTOR PORTÁTIL DE RAIO (treinamento)', '<ul>\r\n	<li>\r\n	<div><span style=\"font-size:14px\"><span style=\"font-family:lucida sans unicode,lucida grande,sans-serif\"><small>S<span style=\"color:rgb(0, 0, 0)\">er&aacute; ministrado pelo Eng.&ordm; Celso Teixeira: um treinamento sobre a utiliza&ccedil;&atilde;o do&nbsp;aparelho detector de raio, incluindo dicas e procedimentos de como&nbsp;evacuar a &aacute;rea&nbsp;com seguran&ccedil;a;</span></small></span></span></div>\r\n	</li>\r\n</ul>\r\n', 1, 0, 0, 0),
(37, 'ELET - Quadro Elétrico (aterramento)', '<ul>\r\n	<li>\r\n	<div><span style=\"font-size:14px\"><span style=\"font-family:lucida sans unicode,lucida grande,sans-serif\"><small>Ser&aacute; realizado o ATERRAMENTO do(s) Quadro(s) El&eacute;trico(s), para garantir a prote&ccedil;&atilde;o do(s) mesmo(s);</small></span></span></div>\r\n	</li>\r\n</ul>\r\n', 1, 0, 0, 0),
(38, 'ELET - Equipamentos elétricos (aterramento)', '<ul>\r\n	<li>\r\n	<div><span style=\"font-size:14px\"><span style=\"font-family:lucida sans unicode,lucida grande,sans-serif\"><small>Ser&aacute; realizado o aterramento de todos os equipamentos el&eacute;tricos, para garantir a prote&ccedil;&atilde;o dos mesmos;</small></span></span></div>\r\n	</li>\r\n</ul>\r\n', 1, 0, 0, 0),
(39, '3ATERR - ATERRAMENTO SPDA', '<ul>\r\n	<li>\r\n	<div><span style=\"font-family:lucida sans unicode,lucida grande,sans-serif\"><span style=\"font-size:11px\"><big><small><span style=\"color:rgb(0, 0, 0)\">Ser&aacute; realizado o aterramento de TODAS as&nbsp;estruturas met&aacute;licas&nbsp;da cobertura (telhados met&aacute;licos, calhas, placas solares, condutores, antenas, etc.) e quaisquer outros elementos, equipamentos&nbsp;e estruturas met&aacute;licas&nbsp;ao redor do im&oacute;vel;</span></small></big></span></span></div>\r\n	</li>\r\n</ul>\r\n', 1, 0, 0, 0),
(40, 'ELET - Adequação/Manutenção Geral', '<ul>\r\n	<li>\r\n	<div><big><span style=\"font-size:11px\"><span style=\"font-family:lucida sans unicode,lucida grande,sans-serif\">Instala&ccedil;&atilde;o de barreira acr&iacute;lica nos quadros el&eacute;tricos (onde houver necessidade);</span></span></big></div>\r\n	</li>\r\n	<li>\r\n	<div><big><span style=\"font-size:11px\"><span style=\"font-family:lucida sans unicode,lucida grande,sans-serif\">Manuten&ccedil;&atilde;o de fios expostos;</span></span></big></div>\r\n	</li>\r\n	<li>\r\n	<div><big><span style=\"font-size:11px\"><span style=\"font-family:lucida sans unicode,lucida grande,sans-serif\">Sinaliza&ccedil;&atilde;o e diagrama unifamiliar dos quadros el&eacute;tricos (onde houver necessidade);</span></span></big></div>\r\n	</li>\r\n	<li>\r\n	<div><big><span style=\"font-size:11px\"><span style=\"font-family:lucida sans unicode,lucida grande,sans-serif\">Limpeza geral&nbsp;dos quadros el&eacute;tricos;</span></span></big></div>\r\n	</li>\r\n	<li>\r\n	<div><big><span style=\"font-size:11px\"><span style=\"font-family:lucida sans unicode,lucida grande,sans-serif\">Reaperto das conex&otilde;es e organiza&ccedil;&atilde;o geral dos condutores dos quadros que necessitam;</span></span></big></div>\r\n	</li>\r\n</ul>\r\n', 1, 0, 0, 0),
(41, '0 - LAUDO SPDA ', '<ul>\r\n	<li>\r\n	<div><span style=\"font-family:lucida sans unicode,lucida grande,sans-serif\"><span style=\"font-size:11px\">Ser&aacute; realizado um Laudo T&eacute;cnico do Sistema de Prote&ccedil;&atilde;o contra raios (SPDA), onde iremos atestar as conformidades e/ou inconformidades encontrada nos 03 (tr&ecirc;s) subsistemas que o comp&otilde;em, de acordo a </span></span><small><span style=\"font-family:lucida sans unicode,lucida grande,sans-serif\"><span style=\"font-size:11px\"><span style=\"color:rgb(0, 0, 0)\">NBR 5.419 &uacute;ltima revis&atilde;o em 2015</span></span></span></small><span style=\"font-family:lucida sans unicode,lucida grande,sans-serif\"><span style=\"font-size:11px\">;</span></span></div>\r\n	</li>\r\n</ul>\r\n', 1, 0, 0, 0),
(42, 'TENSIONAMENTO DE CABOS', '<ul>\r\n	<li>\r\n	<div><small><span style=\"font-family:lucida sans unicode,lucida grande,sans-serif\"><span style=\"font-size:11px\">Tensionamento de cabos&nbsp;existentes, com intuito de melhorar a condu&ccedil;&atilde;o da corrente de descarga do raio recebido pelo terminal a&eacute;reo ou captor at&eacute; o sistema de aterramento, assim como, fixa&ccedil;&atilde;o dos isoladores soltos;</span></span></small></div>\r\n	</li>\r\n</ul>\r\n', 1, 0, 0, 0),
(43, '0 - PROJETO SPDA ', '<ul>\r\n	<li>\r\n	<div><small><span style=\"font-family:lucida sans unicode,lucida grande,sans-serif\"><span style=\"font-size:11px\">Elabora&ccedil;&atilde;o de Projeto do SPDA em AutoCAD, ilustrando a disposi&ccedil;&atilde;o dos 03 (tr&ecirc;s) subsistemas que consistem o Sistema de Prote&ccedil;&atilde;o contra Descargas Atmosf&eacute;rica (SPDA), sendo eles: capta&ccedil;&atilde;o superior, descida e aterramento;&nbsp;</span></span></small><span style=\"font-family:lucida sans unicode,lucida grande,sans-serif; font-size:11px\">com a disponibiliza&ccedil;&atilde;o de 02 (duas) vias do projeto impresso em tamanho A0 com 01 (uma) m&iacute;dia de CD/DVD contendo o projeto elaborado em formato DWG;</span></div>\r\n	</li>\r\n</ul>\r\n', 1, 0, 0, 0),
(44, 'CONECTORES/PARAFUSOS ANTI FERRUGEM', '<ul>\r\n	<li>\r\n	<div style=\"text-align: justify;\"><span style=\"font-family:lucida sans unicode,lucida grande,sans-serif\"><small><span style=\"font-size:12px\">Ser&aacute; realizado a inser&ccedil;&atilde;o de parafusos anti ferrugem ou troca, conforme preconiza NBR 5.419/2015, se necess&aacute;rio for;</span></small></span></div>\r\n	</li>\r\n</ul>\r\n', 1, 0, 0, 0),
(45, 'MASSA CALAFETADORA / MOLDANTE', '<ul>\r\n	<li style=\"text-align:justify\">\r\n	<div><span style=\"font-size:11px\"><big><span style=\"font-family:lucida sans unicode,lucida grande,sans-serif\"><small><span style=\"color:rgb(0, 0, 0)\">Para a perfeita veda&ccedil;&atilde;o das conex&otilde;es, aumentando a vida &uacute;til do material e evitando&nbsp;a oxida&ccedil;&atilde;o&nbsp;precoce (que prejudica o bom contato el&eacute;trico entre o cabo e&nbsp;haste respons&aacute;vel pela eleva&ccedil;&atilde;o da resist&ecirc;ncia do aterramento), ser&aacute; aplicado&nbsp;a&nbsp;massa&nbsp;calafetadora&nbsp; ou moldante (impermeabilizante</span><span style=\"color:rgb(0, 0, 0)\">) apropriada para tal fim; assim como, a aplica&ccedil;&atilde;o&nbsp;<span style=\"color:rgb(0, 0, 0)\">de gel polarizante nas caixas de passagem de solo, para aumentar a condutividade entre eletrodo e solo;</span></span></small></span></big></span></div>\r\n	</li>\r\n</ul>\r\n', 1, 0, 0, 0),
(46, 'AVCB - Sistema de Iluminação de Emergência e de Alarme Contra Incêndio', '<ul>\r\n	<li>\r\n	<div><span style=\"font-size:14px\"><small><span style=\"font-family:lucida sans unicode,lucida grande,sans-serif\">Teste de funcionamento;</span></small></span></div>\r\n	</li>\r\n	<li>\r\n	<div><span style=\"font-size:14px\"><small><span style=\"font-family:lucida sans unicode,lucida grande,sans-serif\">Treinamento de opera&ccedil;&atilde;o da central;</span></small></span></div>\r\n	</li>\r\n	<li>\r\n	<div><span style=\"font-size:14px\"><small><span style=\"font-family:lucida sans unicode,lucida grande,sans-serif\">Passagem dos cabos, fixa&ccedil;&atilde;o das botoeiras;</span></small></span></div>\r\n	</li>\r\n	<li>\r\n	<div><span style=\"font-size:14px\"><small><span style=\"font-family:lucida sans unicode,lucida grande,sans-serif\">Instala&ccedil;&atilde;o de central de ilumina&ccedil;&atilde;o de emerg&ecirc;ncia;</span></small></span></div>\r\n	</li>\r\n	<li>\r\n	<div><span style=\"font-size:14px\"><small><span style=\"font-family:lucida sans unicode,lucida grande,sans-serif\">Instala&ccedil;&atilde;o de lumin&aacute;rias em todos os ambientes precisos;</span></small></span></div>\r\n	</li>\r\n	<li>\r\n	<div><span style=\"font-size:14px\"><small><span style=\"font-family:lucida sans unicode,lucida grande,sans-serif\">Instala&ccedil;&atilde;o de aviso sonoro em caso(s) de emerg&ecirc;ncia(s);</span></small></span></div>\r\n	</li>\r\n</ul>\r\n', 1, 0, 0, 0),
(47, '0 - SPDA (retirada)', '<ul>\r\n	<li>\r\n	<div style=\"text-align: justify;\"><span style=\"font-size:12px\"><small><span style=\"font-family:lucida sans unicode,lucida grande,sans-serif\"><big><span style=\"color:rgb(0, 0, 0)\">Ser&aacute; realizado a retirada de todo o SPDA existente que esteja deixando de cumprir sua fun&ccedil;&atilde;o estabelecida perante a norma&nbsp;da ABNT NBR 5.419, &uacute;ltima revis&atilde;o em 2015, encontrando-se totalmente inconforme e inapropriado, comprometendo a vida de todos os usu&aacute;rios presentes no im&oacute;vel em quest&atilde;o;</span></big></span></small></span></div>\r\n	</li>\r\n</ul>\r\n', 1, 0, 0, 0),
(48, 'AVCB - Projeto Layout', '<ul>\r\n	<li>\r\n	<div><span style=\"font-size:11px\"><big><span style=\"font-family:lucida sans unicode,lucida grande,sans-serif\"><small><span style=\"color:rgb(0, 0, 0)\">Elabora&ccedil;&atilde;o de Projeto para aquisi&ccedil;&atilde;o do AVCB (Auto de Vistoria do Corpo de Bombeiros), de acordo a norma vigente, mediante aprova&ccedil;&atilde;o do corpo de bombeiro;</span></small></span></big></span></div>\r\n	</li>\r\n</ul>\r\n', 1, 0, 0, 0),
(49, 'ELET - Projeto Elétrico', '<ul>\r\n	<li><span style=\"font-family:lucida sans unicode,lucida grande,sans-serif; font-size:11px\">Elabora&ccedil;&atilde;o de Projeto El&eacute;trico de entrada de energia&nbsp;representando as instala&ccedil;&otilde;es el&eacute;tricas do im&oacute;vel;</span></li>\r\n</ul>\r\n', 1, 0, 0, 0),
(50, 'CONEXÕES GERAIS (manutenção)', '<ul>\r\n	<li>\r\n	<div><small><span style=\"font-family:lucida sans unicode,lucida grande,sans-serif\">Ser&aacute; realizado o reaperto das conex&otilde;es, onde houver necessidade, em todo o sistema de prote&ccedil;&atilde;o;</span></small></div>\r\n	</li>\r\n</ul>\r\n', 1, 0, 0, 0),
(51, '3ATERR - Subsistema de aterramento (recomposição)', '<ul>\r\n	<li>\r\n	<div><small><span style=\"font-family:lucida sans unicode,lucida grande,sans-serif\"><span style=\"font-size:11px\"><span style=\"color:rgb(0, 0, 0)\">Ser&aacute; realizado a RECOMPOSI&Ccedil;&Atilde;O e REINTEGRAMENTO dos aterramentos existentes, deixando tudo no mesmo potencial, diminuindo a probabilidade de queima de equipamentos;</span></span></span></small></div>\r\n	</li>\r\n</ul>\r\n', 1, 0, 0, 0),
(53, 'ELET - Cabine(s) de Medição (estudo)', '<ul>\r\n	<li>\r\n	<div><small><span style=\"font-family:lucida sans unicode,lucida grande,sans-serif\"><span style=\"font-size:11px\">Ser&aacute; realizado o estudo da viabiliza&ccedil;&atilde;o para economia e customiza&ccedil;&atilde;o da energia&nbsp;em cabines de: baixa, m&eacute;dia e alta tens&atilde;o;</span></span></small></div>\r\n	</li>\r\n</ul>\r\n', 0, 0, 0, 0),
(54, 'GALVANIZAÇÃO GERAL', '<ul>\r\n	<li>\r\n	<div><span style=\"font-size:12px\"><span style=\"font-family:lucida sans unicode,lucida grande,sans-serif\"><big><small>Ser&aacute; realizada a <strong>galvaniza&ccedil;&atilde;o de todos os elementos</strong>&nbsp;(mastros, isoladores, terminais, parafusos e etc) que possu&iacute;rem partes oxidadas e/ou deterioradas; ou seja,&nbsp;</small></big><span style=\"color:rgb(34, 34, 34)\">aplica&ccedil;&atilde;o de uma camada protetora de Zinco ou ligas de Zinco a uma superf&iacute;cie de a&ccedil;o ou ferro de modo a evitar a corros&atilde;o destes; assim como,&nbsp;</span>podendo ser realizada a troca dos mesmos, se necess&aacute;rios for;<big><small>&nbsp;</small></big></span></span></div>\r\n	</li>\r\n</ul>\r\n', 1, 0, 0, 0),
(55, '3ATERR - Haste(s) de aterramento (manutenção)', '<ul>\r\n	<li>\r\n	<div style=\"text-align: justify;\"><span style=\"font-family:lucida sans unicode,lucida grande,sans-serif\"><span style=\"font-size:11px\"><big><small><span style=\"color:rgb(0, 0, 0)\">Ser&aacute; realizada a MANUTEN&Ccedil;&Atilde;O das hastes copperweld (5/8 x 2,40m) para aterramento;&nbsp;podendo tamb&eacute;m, ser realizada a troca da mesma por outra em estado apropriado, se necess&aacute;rio for;</span></small></big></span></span></div>\r\n	</li>\r\n</ul>\r\n', 1, 0, 0, 0),
(56, '3ATERR - Laudo Aterramento', '<ul>\r\n	<li style=\"text-align:justify\">\r\n	<div><span style=\"font-size:11px\"><big><small><span style=\"font-family:lucida sans unicode,lucida grande,sans-serif\"><span style=\"color:rgb(0, 0, 0)\">Ser&aacute; realizada a inspe&ccedil;&atilde;o visual de todos os pontos de aterramento com medi&ccedil;&otilde;es utilizando-se terr&ocirc;metro e mult&iacute;metro calibrados, com o intu&iacute;to de medir a Resist&ecirc;ncia do Aterramento&nbsp;que deve ser a menor poss&iacute;vel, como preconiza a NBR 5.419 &Uacute;LTIMA REVIS&Atilde;O DE 2015; assim como, a realiza&ccedil;&atilde;o de uma a<span style=\"font-size:11px\"><big><small><span style=\"font-family:lucida sans unicode,lucida grande,sans-serif\"><span style=\"color:rgb(0, 0, 0)\">n&aacute;lise cr&iacute;tica do subsistema de aterramento, sempre baseado nas normas da ABNT: NBR 5.410/08 e 5.419/15;</span></span></small></big></span></span></span></small></big></span></div>\r\n	</li>\r\n</ul>\r\n', 1, 0, 0, 0),
(57, '0 - MEDIÇÃO ÔHMICA', '<ul>\r\n	<li style=\"text-align:justify\">\r\n	<div><span style=\"font-size:11px\"><small><span style=\"font-family:lucida sans unicode,lucida grande,sans-serif\"><big><span style=\"color:rgb(0, 0, 0)\">Ser&aacute; realizada a VERIFICA&Ccedil;&Atilde;O da continuidade </span></big></span></small></span><span style=\"font-size:11px\"><small><span style=\"font-family:lucida sans unicode,lucida grande,sans-serif\"><big><span style=\"color:rgb(0, 0, 0)\">&ocirc;hmica do sistema de aterramento&nbsp;para observar os valores de resist&ecirc;ncia garantindo o bom funcionamento do mesmo, atrav&eacute;s de aparelhos tipo TERR&Ocirc;METRO, sendo fornecido um relat&oacute;rio t&eacute;cnico com as caracter&iacute;sticas do sistema;</span></big></span></small></span></div>\r\n	</li>\r\n</ul>\r\n', 1, 0, 0, 0),
(58, '2DESC - Eletrodutos (manutenção)', '<ul>\r\n	<li>\r\n	<div><big><span style=\"font-family:lucida sans unicode,lucida grande,sans-serif\"><span style=\"font-size:11px\">Ser&aacute; realizada a MANUTEN&Ccedil;&Atilde;O e/ou TROCA dos eletrodutos exstentes, se necess&aacute;rio for;</span></span></big></div>\r\n	</li>\r\n</ul>\r\n', 1, 0, 0, 0),
(59, '3ATERR - Caixa de Inspeção (manutenção)', '<ul>\r\n	<li>\r\n	<div><span style=\"font-size:11px\"><span style=\"font-family:lucida sans unicode,lucida grande,sans-serif\">Ser&aacute; realizado a MANUTEN&Ccedil;&Atilde;O e LIMPEZA da(s) caixa(s) de inspe&ccedil;&atilde;o para melhor funcionamento das conex&otilde;es; assim como, a subsistitui&ccedil;&atilde;o da(s) mesma(s), se necess&aacute;rio for, conforme determina&ccedil;&otilde;es do item 7.2 da NBR&nbsp;5.419-3/2015;</span></span></div>\r\n	</li>\r\n</ul>\r\n', 1, 0, 0, 0),
(60, '3ATERR - ABERTURA E RECOMPOSIÇÃO DE VALAS', '<ul>\r\n	<li style=\"text-align:justify\">\r\n	<div><span style=\"font-size:11px\"><small><big><span style=\"font-family:lucida sans unicode,lucida grande,sans-serif\"><span style=\"color:rgb(0, 0, 0)\">Ser&aacute; realizada Abertura(s) e Recomposi&ccedil;&atilde;o de vala(s) das descidas at&eacute; a(s)&nbsp;haste(s) de aterramento e entre as mesmas (j&aacute; que&nbsp;cada descida necessita de uma haste, e&nbsp;segundo a norma, todas&nbsp;devem ser interligadas&nbsp;entre si, formando um anel que circunda&nbsp;a edifica&ccedil;&atilde;o); N&Atilde;O INCLUSO no nosso escopo a recomposi&ccedil;&atilde;o&nbsp;de jardins (vegeta&ccedil;&atilde;o de qualquer esp&eacute;cie) e assentamento de pisos de quaisquer materiais&nbsp;(cer&acirc;micos, pedras naturais e&nbsp;etc), sendo que o piso ser&aacute; entregue, quando revestido&nbsp;no contra piso&nbsp;para que o contratante finalize os trabalhos e compactado, quando n&atilde;o houver revestimentos;</span></span></big></small></span></div>\r\n	</li>\r\n</ul>\r\n', 1, 0, 0, 0),
(61, 'CONEXÕES GERAIS (instalação)', '<ul>\r\n	<li>\r\n	<div><small><span style=\"font-size:12px\"><span style=\"font-family:lucida sans unicode,lucida grande,sans-serif\">Ser&aacute; realizada a INSTALA&Ccedil;&Atilde;O de&nbsp;(isoladores, terminais, parafusos e etc) anti ferrugem, conforme preconiza a NBR 5.419/2015;</span></span></small></div>\r\n	</li>\r\n</ul>\r\n', 1, 0, 0, 0),
(62, '0 - GAIOLA DE FARADAY (instalação)', '<ul>\r\n	<li>\r\n	<div><span style=\"font-size:14px\"><span style=\"font-family:lucida sans unicode,lucida grande,sans-serif\"><small><span style=\"color:#000000\">Ser&aacute; realizada a INSTALA&Ccedil;&Atilde;O do SPDA no m&eacute;todo de Gaiola de Faraday, conforme ilustra&ccedil;&atilde;o abaixo:</span></small></span></span></div>\r\n	</li>\r\n</ul>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"http://prevetec.com.br/v3/includes/components/ckeditor/kcfinder/upload/images/nova-foto-gaiola.png\" style=\"float:left; height:150px; opacity:0.9; text-align:center; width:300px\" /></p>\r\n', 1, 0, 0, 0),
(65, '1CAPT - Subsistema de Captação', '<ul>\r\n	<li>\r\n	<div><small><span style=\"color:rgb(0, 0, 0); font-family:lucida sans unicode,lucida grande,sans-serif; font-size:11px\"><strong>INSTALA&Ccedil;&Atilde;O&nbsp;DO SUBSISTEMA DE CAPTA&Ccedil;&Atilde;O</strong>, para a capta&ccedil;&atilde;o </span></small><span style=\"color:rgb(0, 0, 0); font-family:lucida sans unicode,lucida grande,sans-serif; font-size:11px\">&nbsp;das descargas sobre a cobertura, conforme projeto e determina&ccedil;&otilde;es da NBR 5.419/2015;</span></div>\r\n	</li>\r\n</ul>\r\n', 1, 0, 0, 0),
(66, '2DESC - Subsistema de Descida ', '<ul>\r\n	<li><small><span style=\"color:rgb(0, 0, 0); font-family:lucida sans unicode,lucida grande,sans-serif; font-size:11px\"><strong>INSTALA&Ccedil;&Atilde;O&nbsp;DO SUBSISTEMA DE DESCIDA</strong></span></small><span style=\"color:rgb(0, 0, 0); font-family:lucida sans unicode,lucida grande,sans-serif; font-size:11px\">, conforme projeto e determina&ccedil;&otilde;es da&nbsp;NBR&nbsp;5.419/2015;</span></li>\r\n</ul>\r\n', 1, 0, 0, 0),
(67, '3ATERR - Subsistema de Aterramento', '<ul>\r\n	<li><small><span style=\"color:rgb(0, 0, 0); font-family:lucida sans unicode,lucida grande,sans-serif; font-size:11px\"><strong>INSTALA&Ccedil;&Atilde;O&nbsp;DO SUBSISTEMA DE ATERRAMENTO</strong></span></small><span style=\"color:rgb(0, 0, 0); font-family:lucida sans unicode,lucida grande,sans-serif; font-size:11px\">, conforme projeto e determina&ccedil;&otilde;es da&nbsp;NBR&nbsp;5.419/2015;</span></li>\r\n</ul>\r\n', 1, 0, 0, 0),
(68, '3ATERR - Malha de Aterramento', '<ul>\r\n	<li><small><span style=\"color:rgb(0, 0, 0); font-family:lucida sans unicode,lucida grande,sans-serif; font-size:11px\">INSTALA&Ccedil;&Atilde;O&nbsp;DA MALHA DE ATERRAMENTO, em cabo de cobre nu de 50 mm&sup2;</span></small><span style=\"color:rgb(0, 0, 0); font-family:lucida sans unicode,lucida grande,sans-serif; font-size:11px\">, conforme projeto e determina&ccedil;&otilde;es da&nbsp;NBR&nbsp;5.419-3/2015;</span></li>\r\n</ul>\r\n', 1, 0, 0, 0);
