(69, '4INTER - INTERLIGAÇÕES GERAIS', '<ul>\r\n	<li><small><span style=\"color:rgb(0, 0, 0); font-family:lucida sans unicode,lucida grande,sans-serif; font-size:11px\">Interliga&ccedil;&atilde;o(&otilde;es) de toda(s) a(s) massa(s) da edifica&ccedil;&atilde;o(&otilde;es) e instala&ccedil;&atilde;o de BEP&nbsp;(Barramento de Equipotencializa&ccedil;&atilde;o&nbsp;Principal)</span></small><small><span style=\"color:rgb(0, 0, 0); font-family:lucida sans unicode,lucida grande,sans-serif; font-size:11px\">,&nbsp;</span></small><span style=\"color:rgb(0, 0, 0); font-family:lucida sans unicode,lucida grande,sans-serif; font-size:11px\">conforme projeto e determina&ccedil;&otilde;es do item 6.2 NBR&nbsp;5.419-3/2015;</span></li>\r\n</ul>\r\n', 1, 0, 0, 0),
(70, '0 - GAIOLA DE FARADAY (manutenção)', '<ul>\r\n	<li>\r\n	<div><span style=\"color:#000000\"><small><span style=\"font-family:lucida sans unicode,lucida grande,sans-serif\">Complementa&ccedil;&atilde;o da gaiola de Faraday existente em fite de alum&iacute;nio 7x8 X 1x8 conforme projeto e determina&ccedil;&otilde;es da NBR&nbsp;5.419/2015;&nbsp;</span></small></span></div>\r\n	</li>\r\n	<li>\r\n	<div><small><span style=\"font-size:11px\"><span style=\"font-family:lucida sans unicode,lucida grande,sans-serif\"><em><strong>NOTA:&nbsp;</strong>Para a complementa&ccedil;&atilde;o da gaiola, nossa equipe ir&aacute; precisar andar no telhado, nosso or&ccedil;amento contempla a nossa instala&ccedil;&atilde;o que utilizamos para linha de vida, caso seja necess&aacute;ria outra forma de linha de vida que n&atilde;o seja a nossa, instala&ccedil;&atilde;o de plataforma, instala&ccedil;&atilde;o de prancha ou coisa similar para acesso ao telhado, iremos enviar proposta complementar para esta finalidade.</em></span></span></small></div>\r\n	</li>\r\n</ul>\r\n', 1, 0, 0, 0),
(71, '1CAPT - Anel de Cintamento intermediário (instalação)', '<ul>\r\n	<li>\r\n	<div><span style=\"font-family:lucida sans unicode,lucida grande,sans-serif\"><small>Instala&ccedil;&atilde;o de&nbsp;anel de cintamento intermedi&aacute;rio, conforme determina&ccedil;&otilde;es do item 5.5.3 da NBR 5419-3/2015;</small></span></div>\r\n	</li>\r\n</ul>\r\n', 0, 0, 0, 0),
(72, '2DESC - Subsistema de Descida (interligação)', '<ul>\r\n	<li><span style=\"color:rgb(0, 0, 0); font-family:lucida sans unicode,lucida grande,sans-serif; font-size:11px\">Ser&aacute; realizada a INTERLIGA&Ccedil;&Atilde;O&nbsp;total</span><span style=\"color:rgb(0, 0, 0); font-family:lucida sans unicode,lucida grande,sans-serif; font-size:11px\">&nbsp;do subsistema de descida com o subsistema de capta&ccedil;&atilde;o e aterramento, conforme determina&ccedil;&otilde;es da&nbsp;</span>NBR<span style=\"color:rgb(0, 0, 0); font-family:lucida sans unicode,lucida grande,sans-serif; font-size:11px\">&nbsp;5.419/2015;</span></li>\r\n</ul>\r\n', 1, 0, 0, 0),
(73, '0 - MEDIÇÃO ÔHMICA (adequação/manutenção)', '<ul>\r\n	<li><span style=\"font-size:11px\"><small><span style=\"font-family:lucida sans unicode,lucida grande,sans-serif\"><big><span style=\"color:rgb(0, 0, 0)\">Ser&aacute; realizada a MELHORIA, por meio de a&ccedil;&otilde;es corretivas, da resist&ecirc;ncia do aterramento de pontos que se encontram com valores elevados, conforme item 5.4.1 da NBR&nbsp;5.419-3/2015</span></big></span></small></span><span style=\"font-size:11px\"><small><span style=\"font-family:lucida sans unicode,lucida grande,sans-serif\"><big><span style=\"color:rgb(0, 0, 0)\">;</span></big></span></small></span></li>\r\n</ul>\r\n', 1, 0, 0, 0);
