INSERT INTO `tipo_servico` (`cd_tipo_servico`, `cd_pdf_config`, `ds_titulo`, `ds_descricao`, `cd_situacao`) VALUES
(3, 12, 'ADEQUAÇÃO SPDA', '<h3 style=\"text-align: center;\"><span style=\"font-size:14px\"><small><span style=\"font-family:lucida sans unicode,lucida grande,sans-serif\"><span style=\"color:rgb(0, 0, 0)\">Ser&aacute; realizada a ADEQUA&Ccedil;&Atilde;O no sistema de para-raios, assim como manuten&ccedil;&atilde;o onde houver necessidade,&nbsp;conforme preconiza a norma da ABNT NBR 5.419/2015</span></span></small></span></h3>\r\n', 1),
(4, 15, 'IMPLANTAÇÃO SPDA', '<h3 style=\"text-align: center;\"><span style=\"font-size:14px\"><small><span style=\"font-family:lucida sans unicode,lucida grande,sans-serif\"><span style=\"color:rgb(0, 0, 0)\">Ser&aacute; realizada a IMPLANTA&Ccedil;&Atilde;O do sistema de para-raios, conforme preconiza a norma da ABNT NBR 5.419/2015</span></span></small></span></h3>\r\n', 1),
(5, 26, 'LAUDO SPDA', '<h3 style=\"text-align: center;\"><span style=\"font-size:14px\"><small><span style=\"font-family:lucida sans unicode,lucida grande,sans-serif\"><span style=\"color:rgb(0, 0, 0)\">Ser&aacute; realizada uma Inspe&ccedil;&atilde;o T&eacute;cnica em todo o sistema de p&aacute;ra-raios existente no&nbsp;im&oacute;vel, contemplando a elabora&ccedil;&atilde;o de um Laudo T&eacute;cnico&nbsp;atestando as&nbsp;conformidades e/ou inconformidades existentes, conforme preconizada a NBR 5.419/2015</span></span></small></span></h3>\r\n', 1),
(6, 32, 'LAUDO NR10', '<h3 style=\"text-align: center;\"><span style=\"font-size:14px\"><small><span style=\"font-family:lucida sans unicode,lucida grande,sans-serif\"><span style=\"color:rgb(0, 0, 0)\">Ser&aacute; realizada uma Inspe&ccedil;&atilde;o T&eacute;cnica em todas as instala&ccedil;&otilde;es el&eacute;tricas&nbsp;do im&oacute;vel, contemplando a elabora&ccedil;&atilde;o de um Laudo T&eacute;cnico&nbsp;atestando as&nbsp;conformidades e/ou inconformidades existentes,&nbsp;de acordo com as disposi&ccedil;&otilde;es presente&nbsp;na Norma Regulamentadora de n&ordm; 10 (NR-10)</span></span></small></span></h3>\r\n', 1),
(8, 20, 'INSPEÇÃO SEMESTRAL SPDA', '<div style=\"text-align: center;\"><span style=\"font-size:14px\"><small><span style=\"font-family:lucida sans unicode,lucida grande,sans-serif\"><span style=\"color:rgb(0, 0, 0)\">De acordo o&nbsp;item 7.3.1 da NBR 5.419 parte 3, &uacute;ltima vers&atilde;o revisada de 2015, <strong>&eacute; obrigat&oacute;rio&nbsp;a realiza&ccedil;&atilde;o de inspe&ccedil;&otilde;es t&eacute;cnicas SEMESTRALMENTE</strong> por um profissional devidamente habilitado, para a verifica&ccedil;&atilde;o das condi&ccedil;&otilde;es reais dos subsistemas que comp&otilde;em o sistema de prote&ccedil;&atilde;o (SPDA), popularmente conhecido como p&aacute;ra-raios</span></span></small></span></div>\r\n', 1),
(9, 12, 'MANUTENÇÃO SPDA', '<h3 style=\"text-align: center;\"><span style=\"font-size:14px\"><small><span style=\"font-family:lucida sans unicode,lucida grande,sans-serif\"><span style=\"color:rgb(0, 0, 0)\">Ser&aacute; realizada a MANUTEN&Ccedil;&Atilde;O no sistema de para-raios, conforme preconiza a norma da ABNT NBR 5.419/2015</span></span></small></span></h3>\r\n', 1),
(10, 45, 'PROJETO SPDA', '<div><span style=\"font-size:16px\"><big><span style=\"font-family:lucida sans unicode,lucida grande,sans-serif\"><small><span style=\"color:#000000\">Ser&aacute; elaborado&nbsp;o&nbsp;<strong>Projeto da planta do&nbsp;SPDA</strong> (Sistema de Prote&ccedil;&atilde;o contra Descargas Atmosf&eacute;ricas) se baseando&nbsp;na cobertura&nbsp;e altura final&nbsp;da edifica&ccedil;&atilde;o, onde nesse local ser&aacute; demonstrado a disposi&ccedil;&atilde;o dos 03 (tr&ecirc;s) subsistemas que o&nbsp;comp&otilde;em, tais como:</span></small></span></big></span></div>\r\n\r\n<div>&nbsp;</div>\r\n\r\n<ul>\r\n	<li>\r\n	<div><span style=\"font-size:16px\"><big><span style=\"font-family:lucida sans unicode,lucida grande,sans-serif\"><small><em><span style=\"color:#000000\"><strong>SUBSISTEMA DE CAPTA&Ccedil;&Atilde;O: </strong>com elementos horizontais (anel perimetral de capta&ccedil;&atilde;o) e verticais (terminais a&eacute;reos e/ou Franklin captor com mastro);</span></em></small></span></big></span></div>\r\n	</li>\r\n</ul>\r\n\r\n<div>&nbsp;</div>\r\n\r\n<ul>\r\n	<li>\r\n	<div><span style=\"font-size:16px\"><big><span style=\"font-family:lucida sans unicode,lucida grande,sans-serif\"><small><em><span style=\"color:#000000\"><strong>SUBSISTEMA DE DESCIDA:</strong> com elementos e conectores verticais;</span></em></small></span></big></span></div>\r\n	</li>\r\n</ul>\r\n\r\n<div>&nbsp;</div>\r\n\r\n<ul>\r\n	<li>\r\n	<div><span style=\"font-size:16px\"><big><span style=\"font-family:lucida sans unicode,lucida grande,sans-serif\"><small><em><span style=\"color:#000000\"><strong>SUBSISTEMA DE ATERRAMENTO: </strong>com elementos horizontais (malha de aterramento) e verticais (hastes de aterramento).</span></em></small></span></big></span></div>\r\n	</li>\r\n</ul>\r\n', 1),
(11, 39, 'ADEQUAÇÃO ELÉTRICA', '<h3 style=\"text-align: center;\"><span style=\"font-size:14px\"><small><span style=\"font-family:lucida sans unicode,lucida grande,sans-serif\"><span style=\"color:rgb(0, 0, 0)\">Ser&aacute; realizada a ADEQUA&Ccedil;&Atilde;O EL&Eacute;TRICA nas instala&ccedil;&otilde;es de eletricidade, assim como manuten&ccedil;&atilde;o onde houver necessidade, de acordo com as disposi&ccedil;&otilde;es presente&nbsp;na Norma Regulamentadora de n&ordm; 10 (NR-10)</span></span></small></span></h3>\r\n', 1),
(12, 39, 'MANUTENÇÃO ELÉTRICA', '<h3 style=\"text-align: center;\"><span style=\"font-size:14px\"><small><span style=\"font-family:lucida sans unicode,lucida grande,sans-serif\"><span style=\"color:rgb(0, 0, 0)\">Ser&aacute; realizada a MANUTEN&Ccedil;&Atilde;O EL&Eacute;TRICA nas instala&ccedil;&otilde;es de eletricidade, de acordo com as disposi&ccedil;&otilde;es presentes na Norma Regulamentadora de n&ordm; 10 (NR-10)</span></span></small></span></h3>\r\n', 1),
(16, 57, 'APRESENTAÇÃO', '', 1),
(17, 61, 'ATERRAMENTO ESTRUTURAS EQUIPAMENTOS', '<div style=\"text-align: center;\"><span style=\"font-size:14px\"><small><span style=\"font-family:lucida sans unicode,lucida grande,sans-serif\"><span style=\"color:rgb(0, 0, 0)\">Ser&aacute; realizado&nbsp;o&nbsp;<strong>ATERRAMENTO de Estruturas Met&aacute;licas e Equipamentos eletr&ocirc;nicos</strong>, conforme determina&ccedil;&otilde;es das norma(s) vigente(s) e Norma Regulamentadora de n&ordm; 10</span></span></small></span></div>\r\n', 1),
(18, 81, 'MALHA ATERRAMENTO', '<h3 style=\"text-align:center\"><span style=\"font-size:14px\"><small><span style=\"font-family:lucida sans unicode,lucida grande,sans-serif\"><span style=\"color:rgb(0, 0, 0)\">Ser&aacute; realizada a IMPLANTA&Ccedil;&Atilde;O&nbsp;DA MALHA DE ATERRAMENTO, conforme preconiza a norma da ABNT NBR 5.419/2015</span></span></small></span></h3>\r\n', 1),
(20, 69, 'LAUDO ATERRAMENTO', '<h3 style=\"text-align: center;\"><span style=\"font-size:14px\"><small><span style=\"font-family:lucida sans unicode,lucida grande,sans-serif\"><span style=\"color:rgb(0, 0, 0)\">Ser&aacute; realizada uma Inspe&ccedil;&atilde;o T&eacute;cnica em todo o subsistema de aterramento, contemplando a elabora&ccedil;&atilde;o de um Laudo T&eacute;cnico&nbsp;atestando as&nbsp;conformidades e/ou inconformidades existentes,&nbsp;de acordo com as disposi&ccedil;&otilde;es presente&nbsp;na norma da ABNT NBR 5.419/2015</span></span></small></span></h3>\r\n', 1);
